import React, { useCallback, useMemo, useRef } from 'react';

import { RouteProp, useRoute } from '@react-navigation/native';
import { useIntl } from 'react-intl';
import { Platform } from 'react-native';
import ViewShot from 'react-native-view-shot';

import {
  Box,
  Button,
  Divider,
  Modal,
  QRCode,
  Typography,
  useToast,
} from '@onekeyhq/components';
import LogoPrimary from '@onekeyhq/components/src/Icon/react/illus/LogoPrimary';
import { copyToClipboard } from '@onekeyhq/components/src/utils/ClipboardUtils';
import platformEnv from '@onekeyhq/shared/src/platformEnv';

import { DiscoverModalRoutes, DiscoverRoutesParams } from '../type';

type RouteProps = RouteProp<
  DiscoverRoutesParams,
  DiscoverModalRoutes.ShareModal
>;

const logo =
  'data:image/png;base64,';

export const ShareView = () => {
  const ref = useRef<ViewShot | null>(null);
  const toast = useToast();
  const intl = useIntl();
  const route = useRoute<RouteProps>();
  const { url } = route.params;
  const name = route.params.name ?? '';
  const { logoURL } = route.params;

  const onCapture = useCallback(async () => {
    if (!platformEnv.isNative) return null;
    const share = (await import('react-native-share')).default;
    const uri = await ref.current?.capture?.();
    if (!share || !uri) {
      toast.show({ title: intl.formatMessage({ id: 'msg__unknown_error' }) });
      return;
    }
    const options = Platform.select({
      ios: {
        activityItemSources: [
          {
            placeholderItem: { type: 'url' as const, content: logo },
            item: {
              default: { type: 'url' as const, content: `file://${uri}` },
            },
            linkMetadata: {
              title: 'Share',
            },
          },
        ],
        failOnCancel: true,
      },
      default: {
        url: `file://${uri}`,
        title: 'Share',
        filename: 'onekey_share.png',
        failOnCancel: true,
      },
    });
    await share.open(options).catch(() => {
      console.log('user cancel share');
    });
  }, [ref, toast, intl]);

  const onCopy = useCallback(() => {
    copyToClipboard(url);
    toast.show({ title: intl.formatMessage({ id: 'msg__copied' }) });
  }, [toast, intl, url]);

  const props = useMemo(
    () =>
      logoURL
        ? {
            logoSize: 50,
            logoMargin: 10,
            logo: { uri: logoURL },
          }
        : {
            logoSize: 0,
            logoMargin: 0,
          },
    [logoURL],
  );

  return (
    <Modal
      footer={null}
      header={intl.formatMessage({ id: 'title__share' })}
      scrollViewProps={{
        children: (
          <Box bg="surface-subdued" alignItems="center" h="full">
            <ViewShot ref={ref}>
              <Box p="6" bg="surface-subdued">
                <Box
                  borderWidth="1"
                  borderColor="border-default"
                  p="6"
                  borderRadius={48}
                  alignItems="center"
                >
                  <Box bg="white" p="6" borderRadius={24}>
                    <QRCode
                      value="https://onekey.so/download"
                      size={256}
                      logoBackgroundColor="white"
                      {...props}
                    />
                  </Box>
                  <Typography.DisplayMedium mt="6" color="text-default">
                    {name}
                  </Typography.DisplayMedium>
                  <Typography.Body1 mt="2" color="text-subdued">
                    {url}
                  </Typography.Body1>
                  <Divider
                    color="divider"
                    w="64"
                    orientation="horizontal"
                    my="6"
                  />
                  <Box flexDirection="row" alignItems="center">
                    <Typography.Body2
                      mr="3"
                      color="text-default"
                      fontWeight={500}
                    >
                      Share With
                    </Typography.Body2>
                    <LogoPrimary width={82} height={25} />
                  </Box>
                </Box>
              </Box>
            </ViewShot>
            <Box px="6" w="full">
              <Button
                w="full"
                type="plain"
                size="xl"
                leftIconName="DuplicateSolid"
                onPress={onCopy}
              >
                Copy URL
              </Button>
              <Button
                w="full"
                mt="6"
                type="basic"
                size="xl"
                leftIconName="ShareSolid"
                onPress={onCapture}
              >
                Share
              </Button>
            </Box>
          </Box>
        ),
      }}
    />
  );
};
